import React,{useState,useEffect} from "react";


export default function Pages_count({setCars,pag,sedCars,car_count}){
    const [page,setPage]=useState([...pag])
    console.log(pag)
   
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
     sedCars(()=>car_count.slice(p*6,(p*6)+6))
     if(pag.includes(p+1) && p>0 && p+1<pag.length)  {
        console.log("ты в иф")
     setPage([p,p+1,p+2]) 
     }
    }
    

    return (
<div>
<button className="pages" onClick={()=>changecars(0)}>f</button>
 {page.map((item,index)=><button key={index} className="pages" onClick={()=>changecars(Number(item)-1)}>{item}</button>)}
 <button  className="pages" onClick={()=>changecars(pag.length-1)}>l</button>
 </div>
    )

}
