import React,{useMemo, useState,useEffect} from "react";
import Card from "./card";
import Maxshow from "./maxShow";


function Main({cars_array}) {
    const cars_count=cars_array
    const [cars,setCars]=useState(cars_count.slice(0,6))
    const [show,setShow]=useState({})
    const [page,setPage]=useState(createPages())
    let pages=Math.ceil(cars_count.length/6)    //Вычисляем количество страниц
    createPages()
    function createPages(){               //Вычисляем массив с индексами страницц
      let result=[]
       for(let i=0;i<pages;i++){
        result.push(i)
       }
     return result
    }
  
const res=createPages()       
function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
setCars(()=>cars_count.slice(p*6,(p*6)+6))
setPage([p-1,p,p+1])
}

function showMax(inf){
setShow(inf)  
 }

  return (
  <>
  <div className="main">
 <div className="main_content">
    {cars.map((item,index)=><div className="card" key={index} onClick={()=>showMax(item)}><Card info={item} /></div>)}
 </div>
 <div className="pages_counter">
 <button  className="pages" onClick={()=>changecars(0)}>1</button>
  {page.map((item,index)=><button key={index} className="pages" onClick={()=>changecars(index)}>{item}</button>)}
  <button className="pages" onClick={()=>changecars(pages-1)}>L</button>
  </div>
 <Maxshow show={show} setShow={setShow}/>
 </div>
 
 </>
  );
}

export default Main;
