import React,{useState,useRef} from "react";


export default function Pages_count({setArayCar,ArayCar,cars}){
    const pageRef=useRef()
    const [page,setPage]=useState([...createPages().slice(0,3)])  
    console.log(cars.length,"carsss")
    function createPages(){               //Функция,вычисляющая массив с индексами страницц
        let result=[]
        for(let i=1;i<=Math.ceil(cars.length/6);i++){
        result.push(i)
        }
    return result
    }
    let max=createPages().length  
    console.log(max,"dlina")
        
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
        console.log(p,"pppp")
        setArayCar([...cars.slice(p*6,(p+1)*6)])
        if(page.includes(p+1) && p>0 && p+1<max) 
        { 
        setPage([p,p+1,p+2]) 
        }
        else if(p===0) setPage([1,2,3])
        else if(p===max-1) {
            console.log("ты тут")
            setPage([max-2,max-1,max])}
        }
    function change(p){                                                     //Замена цвета в выбранном номере
            try{
                document.querySelector(".selected_page").className="pages"
            }
        catch{}
        if(p.className=="pages"){
           
             p.className="selected_page"}
        else p.className="pages"
    }
    return (
<div ref={pageRef}>
<button className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(0)}
    }>{"<"}</button>
 {page.map((item,index)=><button key={index+item} className="pages"   onClick={ (event)=>{ 
    changecars(Number(item)-1)
    change(event.target)
   
}
    }>{item}</button>)}
 <button  className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(max-1)}
    }>{">"}</button>
 </div>
    )

}
