import React,{useState,createContext,useContext} from "react";
import Main from "./main";  
import {cars} from "../data/data";
import {cars_models} from "../data/data_models"
import { ser } from "./App";

function Content() {
  const[ArayCar,setArayCar]=useState(cars)  //Массив с авто в наличии 
  const[text3,setText3]=useState("")
  const[carsAr,setCarsAr]=useState(cars_models)   //Массив с марками


 let context=createContext({ArayCar,setArayCar,text3,setText3,carsAr,setCarsAr})

  function findCars(Mark){
    setArayCar([...cars.filter((item)=>item.marka.toUpperCase()===Mark.toUpperCase())])
    let res=[...cars_models.filter((item)=>item.marka.includes(Mark))]           
    setCarsAr(res)
  }


  function filtred(ev){
    setText3(ev.target.value)
    let res=[...cars_models.filter((item)=>item.marka.includes(ev.target.value))]
    setCarsAr(res)
  }

  function reset(){ 
    setCarsAr([...cars_models])
    setText3("")
    setArayCar([...cars])
  }
  function logshow(e){
    console.log(e.target.parentNode ,"tyr to find select")
  }

return (
  <>
  <context.Provider value={context}>
   <div className="cont">
   <div className="side_bar">
      <div className="input_cars">
        <input type="text" placeholder="марка автомобиля" className="input_side_bar" onChange={(event)=>filtred(event)} value={text3}></input><button type="button" class="btn-close" aria-label="Close" onClick={reset}></button></div>
       {carsAr.map((item,index)=>
      <div className="selected" onClick={(e)=>logshow(e)}>
      <div className="text_logo_marka"><div><p key={index}  className="cars" onClick={() => findCars(item.marka)} >{item.marka.toUpperCase()}</p></div><div className="logo_marka"><img className="logo_images" src={item.icon} alt=""></img></div></div>   
      <div class="form-floating">
  <select class="form-select" id="floatingSelect" aria-label="Floating label select example">
    <option selected>ВСЕ МОДЕЛИ</option>
    {item.models.map((item)=><option value="1" key={item}>{item}</option> )}
  </select>
       
  <label for="floatingSelect">Выберите модель</label>
</div>
      <hr></hr></div>
      )}
    </div>
    <Main ArayCar={ArayCar} cars={cars}  setArayCar={setArayCar} />
    </div>
    </context.Provider >
  </>
);
};
  export default Content
