import React,{useState,useEffect} from "react";
import Main from "./main";  
import data from "./data";

function Content() {
   let cars=data()                                                                      // Импортируем массив со свеми автомобилями

  let[ArayCar,setArayCar]=useState(cars)
  const[text3,setText3]=useState("")
  const[carsAr,setCarsAr]=useState(cars.map(item=>item.marka))
  function findCars(Mark){
  setArayCar([...cars.filter((item)=>item.marka===Mark)])
  }

  function filtred(ev){
    setText3(ev.target.value)
   let Uniq=cars.map(item=>item.marka)                                                    // делаем массив с марками
   
   

    let res=[...UniqCars().filter((item)=>item.includes(ev.target.value))]                 // фильтруем уникальные марки в массив
   setCarsAr(res)
  }

function uniqCars(){
  let uniq=cars.map(item=>item.marka)
  const result = uniq.reduce((acc, item) => {
    if (acc.includes(item)) {
      return acc; // если значение уже есть, то просто возвращаем аккумулятор
    }
    return [...acc, item]; // добавляем к аккумулятору и возвращаем новый аккумулятор
  }, []);
  console.log(result); // => ['


}



function reset(){

  setCarsAr([...cars.map(item=>item.marka)])
  setText3("")
  setArayCar([...cars])
}

  return (
  <>
   <div className="cont">
   <div className="side_bar">
      <div className="input_cars"><input type="text" placeholder="write marka" className="input_side_bar" onChange={(event)=>filtred(event)} value={text3}></input><button className="but_x" onClick={reset} >X</button></div>
      {UniqCars().map((item,index)=><p className="cars" key={index} onClick={()=>findCars(item)}>{item}<hr></hr></p>)}
      
    </div>
    <Main cars_array={ArayCar}  setArayCar={setArayCar}/>
    </div>
    <img src="./logo.png" className="logo"></img>
  </>
);
};
  export default Content
