import React,{useState} from "react";
import Card from "./card";
import Maxshow from "./maxShow";


function Main({cars_array}) {
    const cars_count=cars_array
    const [cars,setCars]=useState(cars_count.slice(0,6))
    const [show,setShow]=useState({})
    let pages=Math.ceil(cars_count.length/6)    //Вычисляем количество страниц
    
    function createPages(){               //Вычисляем количество страниц
       let result=[]
       for(let i=0;i<pages;i++){
        result.push(i)
       }
     return result
    }
  
let res=createPages()
function changecars(p){
setCars(()=>cars_count.slice(p*6,(p*6)+6))
}

function showMax(inf){
 
setShow(inf)  
 }
  return (
  <>
  <div className="main">
 <div className="main_content">
    {cars.map((item,index)=><div className="card"  onClick={()=>showMax(item)}><Card info={item} /></div>)}
 </div>
 <div className="pages_counter">{createPages().map((item,index)=><button key={index} className="pages" onClick={()=>changecars(index)}>{item}</button>)}</div>
 <Maxshow show={show} setShow={setShow}/>
 </div>
 
 </>
  );
}

export default Main;
