import React,{useState,useEffect} from "react";


export default function Pages_count({pag,sedCars,car_count,setCount}){
    const [page,setPage]=useState([...pag.slice(0,3)])
console.log("car_count",car_count)
 
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
     sedCars(()=>car_count.slice(p*6,(p*6)+6))
     if(pag.includes(p+1) && p>0 && p+1<pag.length)  {
    
     setPage([p,p+1,p+2]) 
     }
     else if(p===0) setPage([1,2,3])
     else if(p===pag.length-1) setPage([pag.length-2,pag.length-1,pag.length])
     
    }



    function createPage(){               //Вычисляем массив с индексами страницц
        let result=[]
         for(let i=1;i<=car_count.length;i++){
          result.push(i)
         }
       return result
      }


    function change(p){
        try{
            document.querySelector(".selected_page").className="pages"
        }
        catch{}
        if(p.className=="pages") p.className="selected_page"
        else p.className="pages"
    }
function changeCount(x){
    setCount([x*6,(x*6)+6])
}
    return (
<div>
<button className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(0)}
    }>{"<"}</button>
 {page.map((item,index)=><button key={index} className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(Number(item)-1)
    changeCount(Number(item)-1)}
    }>{item}</button>)}
 <button  className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(pag.length-1)}
    }>{">"}</button>
 </div>
    )

}
