import React,{useState} from "react";
import Maxshow from "./maxShow";


function Card({info}){
    const [show,setShow]=useState(info)
    const [toggle, setToggle] = useState(false);


    function showMax(inf){
        setShow(inf)  
        setToggle(!toggle )
      
    }
    return(
<div className="card" onClick={()=>showMax(info)}>
    <img src="https://kolesa-uploads.ru/-/f8cebc96-e521-4f62-85cf-31b4357decd5/preview-bmw-x5l.jpg" height={"150px"} width={"200px"}></img>
    <ul>
        <li>{info.marka}</li>
        <li>{info.model}</li>
        <li>{info.year}</li>
        <li>{info.information}</li>
    </ul>
 {toggle ?  null :<Maxshow show={show} setToggle={setToggle} toggle={toggle}/>}
</div>
    )
   
}

export default Card
