import React,{useState,useEffect} from "react";
import Main from "./main";  
import {logo} from  "./logo.png"
import data from "./data";

function Content() {
   let cars=data()

  let[ArayCar,setArayCar]=useState(cars)
  const[text3,setText3]=useState("")
  const[carsAr,setCarsAr]=useState(cars.map(item=>item.marka))
  function findCars(Mark){
    console.log(this)
  setArayCar(()=> [...cars.filter((item)=>item.marka===Mark)])
  }

  function filtred(ev){
    setText3(ev.target.value)
   let Uniq=cars.map(item=>item.marka)   // делаем массив с марками
    const resultUniq = Uniq.reduce((acc, item) => {// уникальные марки в массиве
      if (acc.includes(item)) {
        return acc; 
      }
      return [...acc, item]; // 
    }, []);

    let res=[...resultUniq.filter((item)=>item.includes(ev.target.value))]   // фильтруем уникальные марки в массив
   setCarsAr(res)
  }
function reset(){

  setCarsAr([...cars.map(item=>item.marka)])
  setText3("")
  setArayCar([...cars])
}

  return (
  <>
   <div className="cont">
   <div className="side_bar">
      <div className="input_cars"><input type="text" placeholder="write marka" className="input_side_bar" onChange={(event)=>filtred(event)} value={text3}></input><button className="but_x" onClick={reset} >X</button></div>
      {carsAr.map((item,index)=><p className="cars" key={index} onClick={()=>findCars(item)}>{item}<hr></hr></p>)}
      
    </div>
    <Main cars_array={ArayCar}  />
    </div>
    <img src="./logo.png" className="logo"></img>
  </>
);
};
  export default Content
