import React,{useState,useEffect} from "react";


export default function Pages_count({pag,sedCars,car_count}){
    const [page,setPage]=useState([...pag.slice(0,3)])
    const[clas,setClas]=useState(true)
   
   const Active={
       backgroundColor:"black"
   }
   
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
     sedCars(()=>car_count.slice(p*6,(p*6)+6))
     if(pag.includes(p+1) && p>0 && p+1<pag.length)  {
        console.log("ты в иф")
     setPage([p,p+1,p+2]) 
     }
     else if(p===0) setPage([1,2,3])
     else if(p===pag.length-1) setPage([pag.length-2,pag.length-1,pag.length])
     
    }
   
    function change(p){
        setClas(!clas)
        if(p.className=="pages") p.className="selected_page"
        else p.className="pages"
    }

    return (
<div>
<button className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(0)}
    }>{"<"}</button>
 {page.map((item,index)=><button key={index} className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(Number(item)-1)}
    }>{item}</button>)}
 <button  className="pages"  onClick={ (event)=>{ 
    change(event.target)
    changecars(pag.length-1)}
    }>{">"}</button>
 </div>
    )

}
