import React,{useState,useEffect} from "react";


export default function Pages_count({pag,sedCars,car_count}){
    const [page,setPage]=useState([...pag.slice(0,3)])
    console.log(pag)
   
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
     sedCars(()=>car_count.slice(p*6,(p*6)+6))
     if(pag.includes(p+1) && p>0 && p+1<pag.length)  {
        console.log("ты в иф")
     setPage([p,p+1,p+2]) 
     }
     else if(p===0) setPage([1,2,3])
     else if(pag.length-1) setPage([pag.length-3,pag.length-2,pag.length-1])
     
    }
    function change(el){
        el.className="frist"
    }
    

    return (
<div>
<button className="pages" onClick={()=>{  changecars(0)}}>{"<"}</button>
 {page.map((item,index)=><button key={index} className="pages" onClick={()=>changecars(Number(item)-1)}>{item}</button>)}
 <button  className="pages" onClick={()=>changecars(pag.length-1)}>{">"}</button>
 </div>
    )

}
