import React,{useRef,useEffect,useState} from "react";
import {FaAngleRight}  from 'react-icons/fa';
import Modal_show from "./modal_show";


function Card({info,setShow}){
  let imager=useRef()
  let images=info.photos
  const[img,setImg]=useState(images[0])
  useEffect(()=>{
  imager.current.addEventListener('mouseover', changeImg)
  imager.current.addEventListener('mouseout', stopchangeImg)
  imager.current.addEventListener('click', ()=>setShow(info))
  },[])
  let interval
  Math.round(Math.random()*(images.length-1))
      function changeImg(){
        let count=0 
        interval=setInterval(()=>{
          count>images.length-2?count=0:count++
          setImg(images[count])
        },500)
        }
  function stopchangeImg(){
    clearInterval(interval)
    setImg(images[0])
  }
return(<>
  <Modal_show/>
<div className="card mb-3"  ref={imager}  data-bs-toggle="modal" data-bs-target="#exampleModal" >
  <div className="flexiblecard">
    <div></div>
  <div className="card_info">
    <div >
      <img  class="img-fluid rounded-start cardimg"  src={img}      alt="..."></img>
    </div>
    <div >
        <h5 className="cardititle">{info.marka.toUpperCase()}  {info.model}</h5>
        <p className="card-text">{info.year} / <b>{info.price}</b></p> 
    </div>
  </div>
  <div>
  <p className="cardtext">{info.information}</p> 
  </div>
  
  </div>
</div>
</>
    )   
}
export default Card
