import React,{useState,useEffect, useRef} from "react";
import Main from "./main";  
import {cars} from "./data/data";
import useInput from "./hook"
import {cars_models} from "./data/data_models"



function Content({setShow,show}) {
  let[ArayCar,setArayCar]=useState(cars)
  const[text3,setText3]=useState("")
  const[carsAr,setCarsAr]=useState(cars_models)
  function findCars(Mark){
  setArayCar([...cars.filter((item)=>item.marka===Mark)])
  let res=[...uniqCars().filter((item)=>item.includes(Mark))]           
  setCarsAr(res)

  }

  let val=useInput()
   console.log(val.value, "хук юзеинпут")

  function filtred(ev){
    setText3(ev.target.value)
                                                     // делаем массив с марками
    let res1=uniqCars()
    let res=[...res1.filter((item)=>item.includes(ev.target.value))]                 // фильтруем уникальные марки в массив
   setCarsAr(res)
  }

let path=useRef()
console.log(path.current)

function reset(){ 
  path.current.value=""
  setCarsAr([...uniqCars()])
  setText3("")
  setArayCar([...cars])
}

  return (
  <>
   <div className="cont">
   <div className="side_bar">
      <div className="input_cars">
        <input type="text" placeholder="write marka" className="input_side_bar" onChange={(event)=>filtred(event)} value={text3}></input><button type="button" class="btn-close" aria-label="Close" onClick={reset}></button></div>
      {carsAr.map((item,index)=>
      <div className="selected">
      <p key={index}  className="cars" onClick={() => findCars(item.marka)} >{item.marka}</p>
      <div class="form-floating">
  <select class="form-select" id="floatingSelect" aria-label="Floating label select example">
  
    <option selected>ВСЕ МОДЕЛИ</option>
    {item.models.map((item)=><option value="1" key={item}>{item}</option> )}
    <option value="1">One</option>
  
  </select>
  <label for="floatingSelect">Выберите модель</label>
</div>
      
      <hr></hr></div>
      )}
    
    </div>
    <Main ArayCar={ArayCar} cars={cars}  setArayCar={setArayCar} setShow={setShow} show={show}/>
    </div>
    <img src="./logo.png" className="logo"></img>
  </>
);
};
  export default Content
