import React, { useState } from "react";
import Card from "../components/card";
import Pages_count from "./pages";
import Modal_show from "./modal_show";

function Main({ArayCar,setArayCar,cars}) {
  const [show,setShow]=useState({})
  if(ArayCar.length===0) {
    return <div className="main_emty">
      <h2>НЕТ АВТО В НАЛИЧИИИ</h2>
    </div>
  }
    else  return (
  <>
  <div className="main">
 <div className="main_content" >
 <Modal_show show={show} setShow={setShow}/>
    {[...ArayCar.slice(0,6)].map((item,index)=><Card info={item} key={item.marka+Math.random()}  setShow={setShow} />)}
 </div>
 <div className="pages_counter">
  <Pages_count  setArayCar={setArayCar} ArayCar={ArayCar} cars={cars}  />
  </div>
 </div>
 
 </>
  );
}

export default Main;
