import React,{useRef,useEffect,useState} from "react";
import {FaAngleRight}  from 'react-icons/fa';


function Card({info,setShow}){
  let cardDiv=useRef()
  let imager=useRef()
  let images=info.photos
  const[img,setImg]=useState(images[0])
  useEffect(()=>{
  imager.current.addEventListener('mouseover', changeImg)
  imager.current.addEventListener('mouseout', stopchangeImg)
  cardDiv.current.addEventListener('click', ()=>setShow(info))
  },[])
  let interval
  Math.round(Math.random()*(images.length-1))
      function changeImg(){
        let count=0 
        interval=setInterval(()=>{
          count>images.length-2?count=0:count++
          setImg(images[count])
        },500)
        }
  function stopchangeImg(){
    clearInterval(interval)
    setImg(images[0])
  }
return(
  <div class="card"  ref={imager}  data-bs-toggle="modal" data-bs-target="#exampleModal" ref={cardDiv} onClick={()=>{
    console.log("произошел клик")
    setShow(info)}}>
  <div className="card1">
    <div  ><img className="card_img" src={img} height={"160vh"} width={"200vw"} border={"1px solid black"}></img></div>
    
      <div className="opisanie">
      <ul>
     <li ><div className="icon_style"><FaAngleRight/></div><span>{info.marka}</span></li>
        <li><div className="icon_style"><FaAngleRight/></div><span>{info.model}</span></li>
        <li><div className="icon_style"><FaAngleRight/></div><span>{info.year}</span></li>
        <li><div className="icon_style"><FaAngleRight/></div><span>{info.price}</span></li>
        <li><div className="icon_style"><FaAngleRight/></div><span>+79525691651</span></li>
    </ul>
    </div>
    </div>
    <div ><hr className="hr_card"></hr></div>
    <div className="card2">
        <div className="information"><p >{info.information}</p></div></div>
</div>

    )   
}
export default Card
