import React,{useState,useEffect} from "react";


export default function Pages_count({setCars,pag,sedCars,cars}){
    const [page,setPage]=useState(["<",...pag,">"])
    console.log(pag.length)
    if(pag.length>3){
    setPage(["<",...pag,">"])}
    else {
        setPage([...pag])}

   


  
    function changecars(p){                //Устанавливаем в сет 6 карточек с выбранной страницы
     sedCars(()=>cars.slice(p*6,(p*6)+6))
     if(p>2)  
     setPage([]) 
    }

    return (
<div>
 {page.map((item,index)=><button key={index} className="pages" onClick={()=>changecars(index+1)}>{item+1}</button>)}
</div>
    )

}
